sp_widget.client_script:
function($scope, $window, $location) {
  /* widget controller */
  var c = this;
	c.firstPage = true;
	$scope.secondPage = function(){
		c.firstPage = false;
		var remortgageUpdate = document.querySelector('input[name="RemortgageUpdate"]:checked').value;

		if(c.data.sys_id && remortgageUpdate == 'true'){
			c.listPage = true;
		}else if(!c.data.sys_id && remortgageUpdate == 'true'){
			c.listPage = true;
			c.noRemortgage = true;
		}
		else if(c.data.sys_id && remortgageUpdate == 'false'){
			$location.url('mbc?id=remortgage_details&table=x_snc_barclay_remo_remortgage_applications&sys_id='+ c.data.sys_id + '&view=barclay');
		}else if(!c.data.sys_id && remortgageUpdate == 'false'){
			c.listPage = true;
			c.noRemortgage = true;
		}
	}
	$(document).ready(function(){
		$('input[name=RemortgageUpdate]').click(function(){
			document.getElementById("continue").classList.remove("disabled");
		})
	});
	$(document).ready(function(){
		$('input[name=Category]').click(function(){
			document.getElementById("SecondContinue").classList.remove("disabled");
		})
	});
	$(document).ready(function(){
		$('input[name=extraInformation]').click(function(){
			document.getElementById("extraContinue").classList.remove("disabled");
		})
	});
	$scope.detailsPage = function(){
		c.details = document.querySelector('input[name=Category]:checked').value;
		if(c.details == 'employment'){
			c.employmentPage = true;
			c.listPage = false;
		} else{
			c.listPage = false;
			c.affordabilityPage = true;
		}
	}
	$scope.extraInformation = function(){
		c.otherPage = false;
		var extraInformation = document.querySelector('input[name="extraInformation"]:checked').value;

		if(extraInformation == 'true'){
			c.listPage = true;
		}else{
			$location.url('mbc?id=remortgage_details&table=x_snc_barclay_remo_remortgage_applications&sys_id='+ c.data.sys_id + '&view=barclay');
		}
	}
	$scope.submitAffordabilityDetails = function(){
		c.affordabilityPage = false;
		c.otherPage = true;
		c.data.affordDetails = true;
		c.server.update();
	}
	$scope.submitEmploymentDetails = function(){
		c.employmentPage = false; 
		c.otherPage = true; 
		c.data.detailSubmit = true;
		c.data.startDate = c.data.startYear + "-" + c.data.startMonth + "-" + c.data.startDay;
		c.server.update();
	}
}

