sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var remortgage = new GlideRecord('x_snc_barclay_remo_remortgage_applications');
	remortgage.addQuery('opened_by', gs.getUserID());
	remortgage.query();
	var noRemortgage = false;
	if(remortgage.next()){
		data.sys_id = remortgage.sys_id.getDisplayValue();
		data.employerName = remortgage.employer_name.getDisplayValue();
		data.employerAddress = remortgage.employer_address.getDisplayValue();
		data.employerPostcode = remortgage.postcode.getDisplayValue();
		data.employerCountry = remortgage.country.getDisplayValue();
		data.employmentType = remortgage.getValue('employment_type');
		data.employerStatus = remortgage.employer_status.getDisplayValue();
		data.occupation = remortgage.occupation.getDisplayValue();
		data.workPhone = remortgage.work_telephone_number.getDisplayValue();
		var startDate = remortgage.employment_start_date.getDisplayValue();
		startDate = startDate.split('-');
		data.startDay = parseInt(startDate[2]);
		data.startMonth = parseInt(startDate[1]);
		data.startYear = parseInt(startDate[0]);
		data.incomeType = remortgage.income_type.getDisplayValue();
		data.income = remortgage.income_amount.getDisplayValue();
		data.income = data.income.split('£');
		data.income = data.income[1];
		data.incomeFrequency = remortgage.income_frequency.getDisplayValue();
		data.paycheckAmount = remortgage.net_income.getDisplayValue();
		data.paycheckAmount = data.paycheckAmount.split('£');
		data.paycheckAmount = data.paycheckAmount[1];
		data.revaluation = remortgage.re_valuation_of_assets_last_2_years.getDisplayValue();
		data.revaluationDetails = remortgage.re_valuation_details.getDisplayValue();
		data.recurringItem = remortgage.non_recurring_items.getDisplayValue();
		data.itemAmount = remortgage.amount_of_non_recurring_items.getDisplayValue();
		data.recurringDetails = remortgage.non_recurring_items_details.getDisplayValue();
		data.otherLoans = remortgage.other_loans.getDisplayValue();
		data.commitments = remortgage.other_commitments.getDisplayValue();
		data.pre = remortgage.property_related_expenses.getDisplayValue();
		data.changes = remortgage.personal_changes.getDisplayValue();
		data.totalSavings = remortgage.total_savings.getDisplayValue();
		data.totalSavings = data.totalSavings.split('£');
		data.totalSavings = data.totalSavings[1];
		data.totalInvestments = remortgage.total_investments.getDisplayValue();
		data.totalInvestments = data.totalInvestments.split('£');
		data.totalInvestments = data.totalInvestments[1];
		data.propertyValue = remortgage.property_value.getDisplayValue();
		data.propertyValue = data.propertyValue.split('£');
		data.propertyValue = data.propertyValue[1];
		data.purchasePrice = remortgage.purchase_price.getDisplayValue();
		data.purchasePrice = data.purchasePrice.split('£');
		data.purchasePrice = data.purchasePrice[1];
		data.totalLoan = remortgage.total_required.getDisplayValue();
		data.totalLoan = data.totalLoan.split('£');
		data.totalLoan = data.totalLoan[1];
		data.portExisting = remortgage.existing_product.getDisplayValue();
		data.productCode = remortgage.product_code.getDisplayValue();
		data.addedFee = remortgage.customer_added_fee.getDisplayValue();
		data.depositAmount = remortgage.deposit_amount.getDisplayValue();
		data.depositAmount = data.depositAmount.split('£');
		data.depositAmount = data.depositAmount[1];
		data.depositSource = remortgage.source_deposit.getDisplayValue();
		data.repaymentMethod = remortgage.repayment_method.getDisplayValue();
		data.yearLength = remortgage.year_length.getDisplayValue();
		data.monthLength = remortgage.month_length.getDisplayValue();
		data.numApplicants = parseInt(remortgage.total_applicants.getDisplayValue());
		data.numChildDependants = parseInt(remortgage.child_dependents.getDisplayValue());
		data.numAdultDependants = parseInt(remortgage.adult_dependents.getDisplayValue());
		data.purpose = remortgage.loan_purpose.getDisplayValue();
		data.paymentMethod = remortgage.payment_method.getDisplayValue();
		var payDate = remortgage.pay_date.getDisplayValue();
		payDate = payDate.split('-');
		data.payDay = parseInt(payDate[2]);
		data.payMonth = parseInt(payDate[1]);
		data.payYear = parseInt(payDate[0]);
		data.repayStrategy = remortgage.repay_strategy.getDisplayValue();
	} else{
			noRemortgage = true;
	}
	if(input && input.detailSubmit){
		if(noRemortgage){
			remortgage = new GlideRecord('x_snc_barclay_remo_remortgage_applications');
		}
		remortgage.setValue('employer_name' , input.employerName );
		remortgage.setValue('employer_address' , input.employerAddress );
		remortgage.setValue('postcode' , input.employerPostcode );
		remortgage.setValue('country' , input.employerCountry );
		remortgage.setValue('employment_type' , input.employmentType );
		remortgage.setValue('employer_status', input.employerStatus);
		remortgage.setValue('occupation' , input.occupation );
		remortgage.setValue('work_telephone_number' , input.workPhone );
		remortgage.setValue('employment_start_date' , input.startDate );
		remortgage.setValue('income_type' , input.incomeType );
		remortgage.setValue('income_amount' , "GBP;" + input.income);
		remortgage.setValue('income_frequency' , input.incomeFrequency );
		remortgage.setValue('net_income' , "GBP;" + input.paycheckAmount);
		remortgage.setValue('re_valuation_of_assets_last_2_years' , input.revaluation );
		remortgage.setValue('re_valuation_details' , input.revaluationDetails );
		remortgage.setValue('non_recurring_items' , input.recurringItem );
		remortgage.setValue('amount_of_non_recurring_items' , input.itemAmount );
		remortgage.setValue('non_recurring_items_details' , input.recurringDetails );
		if(noRemortgage){
			remortgage.insert();
			data.sys_id = remortgage.sys_id.getDisplayValue();
		}else{
			remortgage.update();
		}

	}
	if(input && input.affordDetails){
		if(noRemortgage){
			remortgage = new GlideRecord('x_snc_barclay_remo_remortgage_applications');
		}
		remortgage.setValue('other_loans' , input.otherLoans );
		remortgage.setValue('other_commitments' , input.commitments );
		remortgage.setValue('property_related_expenses' , input.pre );
		remortgage.setValue('personal_changes' , input.changes );
		remortgage.setValue('total_savings' , "GBP;" + input.totalSavings );
		remortgage.setValue('total_investments', "GBP;" + input.totalInvestments);
		remortgage.setValue('property_value' , "GBP;" + input.propertyValue );
		remortgage.setValue('purchase_price' , "GBP;" + input.purchasePrice );
		remortgage.setValue('total_required' , "GBP;" + input.totalLoan );
		remortgage.setValue('existing_product' , input.portExisting );
		remortgage.setValue('product_code' , input.productCode );
		remortgage.setValue('customer_added_fee' , input.addedFee );
		remortgage.setValue('deposit_amount' , "GBP;" + input.depositAmount);
		remortgage.setValue('source_deposit' , input.depositSource );
		remortgage.setValue('repayment_method' , input.repaymentMethod );
		remortgage.setValue('year_length' , input.yearLength );
		remortgage.setValue('month_length' , input.monthLength );
		remortgage.setValue('total_applicants' , input.numApplicants );
		remortgage.setValue('child_dependents' , input.numChildDependants );
		remortgage.setValue('adult_dependents' , input.numAdultDependants );
		remortgage.setValue('loan_purpose' , input.purpose );
		remortgage.setValue('payment_method' , input.paymentMethod );
		remortgage.setValue('pay_date' , input.payYear + "-" + input.payMonth + "-" + input.payDay);
		remortgage.setValue('repay_strategy' , input.repayStrategy );
		if(noRemortgage){
			remortgage.insert();
			data.sys_id = remortgage.sys_id.getDisplayValue();
		}else{
			remortgage.update();
		}

	}
})();
